list-view-config.query-select:
PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?subclass ?role ?activity ?activityName ?activityLabel ?activityTypeLabel ?awardedBy ?adminedBy ?awardedByLabel ?adminedByLabel ?dateTimeStartRole ?dateTimeEndRole ?dateTimeStartGrant ?dateTimeEndGrant ?hideThis ?localId
WHERE {
?subject ?property ?role . ?role a core:PrincipalInvestigatorRole .
OPTIONAL { ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . ?activity rdfs:label ?activityLabel . LET (?activityName := afn:localname(?activity)) }
OPTIONAL { ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity .
?activity vitro:mostSpecificType ?subclass .
}
OPTIONAL { ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . ?activity vitro:mostSpecificType ?subclass . ?subclass rdfs:label ?activityTypeLabel }
OPTIONAL { ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . ?activity <http://connect.unavco.org/ontology/vlocal#has4CharID> ?localId . }
OPTIONAL { ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . ?activity core:assignedBy ?awardedBy . ?awardedBy rdfs:label ?awardedByLabel } OPTIONAL { ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . ?activity core:relates ?adminedByRole . ?adminedByRole <http://purl.obolibrary.org/obo/RO_0000052> ?adminedBy . ?adminedBy rdfs:label ?adminedByLabel } OPTIONAL { ?role core:dateTimeInterval ?dateTimeIntervalRole . ?dateTimeIntervalRole core:start ?dateTimeStartValueRole . ?dateTimeStartValueRole core:dateTime ?dateTimeStartRole } OPTIONAL { ?role core:dateTimeInterval ?dateTimeIntervalRole . ?dateTimeIntervalRole core:end ?dateTimeEndValueRole . ?dateTimeEndValueRole core:dateTime ?dateTimeEndRole } OPTIONAL { ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . ?activity core:dateTimeInterval ?dateTimeIntervalGrant . ?dateTimeIntervalGrant core:start ?dateTimeStartValueGrant . ?dateTimeStartValueGrant core:dateTime ?dateTimeStartGrant } OPTIONAL { ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . ?activity core:dateTimeInterval ?dateTimeIntervalGrant . ?dateTimeIntervalGrant core:end ?dateTimeEndValueGrant . ?dateTimeEndValueGrant core:dateTime ?dateTimeEndGrant } OPTIONAL { ?role core:hideFromDisplay ?hideThis } } ORDER BY ?subclass DESC(?dateTimeEndRole) DESC(?dateTimeStartRole) DESC(?dateTimeEndGrant) DESC(?dateTimeStartGrant) ?activityLabel ?activityName ?activityTypeLabel

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> 
PREFIX foaf: <http://xmlns.com/foaf/0.1/>  
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> 
CONSTRUCT { 
    ?subject ?property ?role .
    ?role a core:PrincipalInvestigatorRole .
    ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity .
    ?role ?roleProperty ?roleValue . 
    ?activity rdfs:label ?activityLabel .
    ?activity vitro:mostSpecificType ?subclass .
    ?subclass rdfs:label ?activityTypeLabel .
    ?activity core:assignedBy ?awardedBy .
    ?awardedBy rdfs:label ?awardedByLabel .
    ?activity core:relates ?adminedByRole .
    ?adminedByRole <http://purl.obolibrary.org/obo/RO_0000052> ?adminedBy .
    ?adminedBy rdfs:label ?adminedByLabel


} WHERE {
    {
        ?subject ?property ?role .
        ?role a core:PrincipalInvestigatorRole . 
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity .


    } UNION {
        ?subject ?property ?role .
        ?role a core:PrincipalInvestigatorRole .
        ?role ?roleProperty ?roleValue 
    } UNION {
        ?subject ?property ?role .
        ?role a core:PrincipalInvestigatorRole .
        ?role core:relatedBy ?activity . 
        ?activity rdfs:label ?activityLabel 
    } UNION {
       ?subject ?property ?role .
       ?role a core:PrincipalInvestigatorRole .
       ?role core:relatedBy ?activity .
       ?activity vitro:mostSpecificType ?subclass 
    } UNION {
       ?subject ?property ?role .
       ?role a core:PrincipalInvestigatorRole .
       ?role core:relatedBy ?activity .
       ?activity vitro:mostSpecificType ?subclass . 
       ?subclass rdfs:label ?activityTypeLabel
    } UNION {
        ?subject ?property ?role .
        ?role a core:PrincipalInvestigatorRole .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity .
        ?activity core:assignedBy ?awardedBy .
        ?awardedBy a foaf:Organization .
        ?awardedBy rdfs:label ?awardedByLabel
    } UNION {
        ?subject ?property ?role .
        ?role a core:PrincipalInvestigatorRole .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . 
        ?activity core:relates ?adminedByRole .
        ?adminedByRole a core:AdminsitratorRole.
        ?adminedByRole <http://purl.obolibrary.org/obo/RO_0000052> ?adminedBy .
        ?adminedBy a foaf:Organization .
        ?adminedBy rdfs:label ?adminedByLabel
    } UNION {
        ?subject ?property ?role .
        ?role a core:PrincipalInvestigatorRole .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . 
        ?activity a core:Contract .
        ?activity rdfs:label ?activityLabel 
    } 
}

PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> CONSTRUCT { ?subject ?property ?role . ?role a core:PrincipalInvestigatorRole . ?activity core:dateTimeInterval ?dateTimeIntervalGrant . ?dateTimeIntervalGrant core:start ?dateTimeStartValueGrant . ?dateTimeStartValueGrant core:dateTime ?dateTimeStartGrant } WHERE {
?subject ?property ?role . ?role a core:PrincipalInvestigatorRole . ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . ?activity core:dateTimeInterval ?dateTimeIntervalGrant . ?dateTimeIntervalGrant core:start ?dateTimeStartValueGrant . ?dateTimeStartValueGrant core:dateTime ?dateTimeStartGrant
}

PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> CONSTRUCT { ?subject ?property ?role . ?role a core:PrincipalInvestigatorRole . ?activity core:dateTimeInterval ?dateTimeIntervalGrant . ?dateTimeIntervalGrant core:end ?dateTimeEndValueGrant . ?dateTimeEndValueGrant core:dateTime ?dateTimeEndGrant } WHERE {
?subject ?property ?role . ?role a core:PrincipalInvestigatorRole . ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . ?activity core:dateTimeInterval ?dateTimeIntervalGrant . ?dateTimeIntervalGrant core:end ?dateTimeEndValueGrant . ?dateTimeEndValueGrant core:dateTime ?dateTimeEndGrant .
}

PREFIX core: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?role .
    ?role a core:PrincipalInvestigatorRole .  
    ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity .
    ?activity <http://connect.unavco.org/ontology/vlocal#has4CharID> ?localId . 

} WHERE {
    ?subject ?property ?role .  
    ?role a core:PrincipalInvestigatorRole .
    ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity .
    ?activity a <http://connect.unavco.org/ontology/vlocal#Station> .
    ?activity <http://connect.unavco.org/ontology/vlocal#has4CharID> ?localId  
}
