list-view-config.query-select:
PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX obo: <http://purl.obolibrary.org/obo/> PREFIX vcard: <http://www.w3.org/2006/vcard/ns#> PREFIX fn: <http://www.w3.org/2005/xpath-functions#>
SELECT ?person ?personName ?posnLabel ?org ?orgLabel ?title ?parentOrg ?parentOrgLabel
WHERE { ?subject ?property ?person . OPTIONAL { ?person core:relatedBy ?position . ?position a core:Position } OPTIONAL { ?person core:relatedBy ?position . ?position a core:Position . ?position rdfs:label ?posnLabel } OPTIONAL { ?person rdfs:label ?personName } OPTIONAL { ?person obo:ARG_2000028 ?vcard . ?vcard vcard:hasTitle ?titleObj . ?titleObj vcard:title ?title } OPTIONAL { ?person core:relatedBy ?position . ?position a core:Position . ?position core:relates ?org . ?org a foaf:Organization . ?org rdfs:label ?orgLabel } OPTIONAL { ?person core:relatedBy ?position . ?position a core:Position . ?position core:relates ?org . ?org a foaf:Organization . ?org obo:BFO_0000050 ?parentOrg . ?parentOrg rdfs:label ?parentOrgLabel . }
} ORDER BY (fn:lower-case(?personName))

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX obo: <http://purl.obolibrary.org/obo/>

CONSTRUCT {
    ?subject ?property ?person .
    ?person core:relatedBy ?position .
    ?position a core:Position .
    ?position rdfs:label ?posnLabel .
    ?position core:relates ?org .
    ?org a foaf:Organization .
    ?org rdfs:label ?orgName .
    ?org obo:BFO_0000050 ?parentOrg .
    ?parentOrg rdfs:label ?parentOrgLabel .
} WHERE {
    { 
        ?subject ?property ?person 
    } UNION {                
        ?subject ?property ?person .
        ?person core:relatedBy ?position .
        ?position a core:Position .
        FILTER NOT EXISTS {?position core:dateTimeInterval ?dateTimeInterval .
                    ?dateTimeInterval core:end ?endDate .
                    ?endDate core:dateTime ?endDateValue}
    } UNION {      
        ?subject ?property ?person .
        ?person core:relatedBy ?position .
        ?position a core:Position .
        ?position rdfs:label ?posnLabel .
        FILTER NOT EXISTS {?position core:dateTimeInterval ?dateTimeInterval .
                    ?dateTimeInterval core:end ?endDate .
                    ?endDate core:dateTime ?endDateValue}
    } UNION {      
        ?subject ?property ?person .
        ?person core:relatedBy ?position .
        ?position a core:Position .
        ?position core:relates ?org .
        ?org a foaf:Organization .
        FILTER NOT EXISTS {?position core:dateTimeInterval ?dateTimeInterval .
                    ?dateTimeInterval core:end ?endDate .
                    ?endDate core:dateTime ?endDateValue}

    } UNION {      
        ?subject ?property ?person .
        ?person core:relatedBy ?position .
        ?position a core:Position .
        ?position core:relates ?org .
        ?org a foaf:Organization .
        ?org rdfs:label ?orgName .
        FILTER NOT EXISTS {?position core:dateTimeInterval ?dateTimeInterval .
                    ?dateTimeInterval core:end ?endDate .
                    ?endDate core:dateTime ?endDateValue}
    } UNION {
        ?subject ?property ?person .
        ?person core:relatedBy ?position .
        ?position a core:Position .
        ?position core:relates ?org .
        ?org a foaf:Organization .
        ?org obo:BFO_0000050 ?parentOrg .
        ?parentOrg rdfs:label ?parentOrgLabel .
        FILTER NOT EXISTS {?position core:dateTimeInterval ?dateTimeInterval .
                    ?dateTimeInterval core:end ?endDate .
                    ?endDate core:dateTime ?endDateValue}
    } UNION {
        ?subject ?property ?person .
        ?person core:relatedBy ?position .
        ?position a core:Position .
        ?position core:relates ?org .
        ?org a foaf:Organization .
        ?org obo:BFO_0000050 ?parentOrg .
        ?parentOrg rdfs:label ?parentOrgLabel .
        FILTER NOT EXISTS {?position core:dateTimeInterval ?dateTimeInterval .
                    ?dateTimeInterval core:end ?endDate .
                    ?endDate core:dateTime ?endDateValue}
    }
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>

CONSTRUCT {
    ?subject ?property ?person .
    ?person rdfs:label ?personName .
    ?person obo:ARG_2000028 ?vcard . 
    ?vcard vcard:hasTitle ?titleObj .
    ?titleObj vcard:title ?title
} WHERE {
    { 
        ?subject ?property ?person 
    } UNION {                
        ?subject ?property ?person .
        ?person rdfs:label ?personName  
     } UNION {      
        ?subject ?property ?person .
        ?person obo:ARG_2000028 ?vcard . 
        ?vcard vcard:hasTitle ?titleObj .
        ?titleObj vcard:title ?title
    } 
}
